SELECT MIN("players_teams"."id") as agg_0, SUM("players_teams"."year") as agg_1 FROM "players_teams" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID" LEFT OUTER JOIN "awards_players" ON "players"."playerID" = "awards_players"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID"  WHERE "awards_players"."lgID" LIKE '%N%BA%' AND "players"."weight" >= 228.96055578606672 AND "players_teams"."lgID" IN ('ABA', 'PBLA', 'NBL', 'ABL1') AND ("players_teams"."PostftAttempted" <= 0 OR "players_teams"."PostftAttempted" <= 273 OR "players_teams"."PostftAttempted" <= 0) AND "players_teams"."ftAttempted" <= 67 AND "teams"."name" = 'Oklahoma City Drillers'