SELECT SUM("player_allstar"."ft_made") as agg_0, MAX("player_allstar"."personal_fouls") as agg_1 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID"  WHERE "player_allstar"."league_id" = 'ABA' AND "player_allstar"."first_name" IN ('Kevin', 'Bob', 'Larry', 'Dave') AND "awards_players"."lgID" != 'ABA' AND "players"."pos" NOT LIKE '%G-%F%' AND "awards_players"."year" = 2009