SELECT MAX("players"."height") as agg_0, AVG("players"."weight") as agg_1, MIN("awards_players"."playerID") as agg_2 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID"  WHERE "awards_players"."note" LIKE '%t%ie%' AND "players"."birthDate" IN ('1962-03-26', '1945-02-23', '1947-08-26') AND "awards_players"."playerID" >= 275338 AND "awards_players"."award" LIKE '%Impro%ved%' AND "players"."birthState" != 'QU'