SELECT MIN("players_teams"."GS" + "players_teams"."GP") as agg_0, MIN("teams"."o_to" + "teams"."lost") as agg_1, SUM("players_teams"."playerID" + "players_teams"."blocks") as agg_2 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID"  WHERE "awards_players"."playerID" >= 9252 AND "players"."highSchool" LIKE '%West%' AND "player_allstar"."turnovers" <= 0.8593220934704022 AND "awards_players"."lgID" LIKE '%N%BL%' AND "player_allstar"."rebounds" BETWEEN 0.9809427453853682 AND 2.0