SELECT MIN("series_post"."tmIDLoser" + "series_post"."id") as agg_0 FROM "series_post" LEFT OUTER JOIN "teams" ON "series_post"."tmIDLoser" = "teams"."tmID" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID"  WHERE "teams"."homeLost" <= 9 AND "series_post"."round" = 'FR' AND "coaches"."lgID" IN ('PBLA', 'NPBL', 'ABA', 'NBA')