SELECT AVG("customers"."CustomerID") as agg_0, AVG("gasstations"."ChainID") as agg_1, COUNT(*) as agg_2 FROM "transactions_1k" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "gasstations"."Segment" IN ('Noname', 'Other', 'Discount', 'Premium') AND "products"."Description" IN ('Provozn√≠ poplatek')