SELECT AVG("customers"."CustomerID") as agg_0, MAX("yearmonth"."Consumption" + "yearmonth"."CustomerID") as agg_1, MIN("customers"."CustomerID" + "yearmonth"."Consumption") as agg_2 FROM "customers" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "customers"."Currency" IN ('EUR') AND "customers"."Segment" NOT LIKE '%SME%' AND "customers"."Currency" LIKE '%C%ZK%' AND "yearmonth"."CustomerID" <= 1293149