SELECT SUM("yearmonth"."CustomerID") as agg_0, SUM("transactions_1k"."CustomerID") as agg_1, SUM("transactions_1k"."CardID" + "yearmonth"."Date") as agg_2 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "yearmonth"."Consumption" BETWEEN 1529.615711570201 AND 2529.0409593318514 AND "gasstations"."Segment" LIKE '%Nona%me%' AND "gasstations"."ChainID" <= 8 AND "gasstations"."Country" NOT LIKE '%SVK%' AND "customers"."Segment" NOT LIKE '%SM%E%'