SELECT AVG("products"."ProductID" + "transactions_1k"."CustomerID") as agg_0, SUM("transactions_1k"."GasStationID" + "yearmonth"."CustomerID") as agg_1, MAX("customers"."CustomerID") as agg_2 FROM "customers" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "transactions_1k"."Date" = '2012-08-24' AND ("customers"."Currency" IN ('CZK') OR ("customers"."Currency" = 'CZK' AND "customers"."Currency" NOT LIKE '%E%UR%')) AND "yearmonth"."Consumption" <= 292.5154458703697