SELECT MIN("transactions_1k"."TransactionID") as agg_0, SUM("transactions_1k"."CardID" + "transactions_1k"."GasStationID") as agg_1 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "transactions_1k"."TransactionID" >= 938 AND "gasstations"."ChainID" BETWEEN 1 AND 62 AND "customers"."Currency" != 'CZK' AND ("gasstations"."GasStationID" <= 3661 OR "gasstations"."Country" = 'SVK')