SELECT MAX("transactions_1k"."CustomerID") as agg_0 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "transactions_1k"."Time" = '06:31:00' AND "gasstations"."Country" IN ('SVK') AND ("transactions_1k"."Date" NOT LIKE '%2012-0%8-25%' OR "transactions_1k"."CustomerID" BETWEEN 1263843 AND 1453802) AND "gasstations"."Segment" LIKE '%Di%scount%' AND ("customers"."Segment" = 'KAM' OR "customers"."Currency" LIKE '%CZK%' OR "customers"."Segment" LIKE '%LAM%')