SELECT COUNT(*) as agg_0, SUM("gasstations"."GasStationID" + "yearmonth"."Consumption") as agg_1, MAX("transactions_1k"."Price" + "transactions_1k"."CustomerID") as agg_2 FROM "transactions_1k" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "products"."ProductID" <= 538 AND "yearmonth"."CustomerID" >= 194923