SELECT COUNT(*) as agg_0, SUM("transactions_1k"."CustomerID") as agg_1, COUNT(*) as agg_2 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID"  WHERE "transactions_1k"."CustomerID" BETWEEN 776989 AND 1076444 AND ("transactions_1k"."Date" IN ('2012-08-26', '2012-08-24', '2012-08-23') OR "transactions_1k"."TransactionID" >= 391 OR "transactions_1k"."Date" LIKE '%2012-%08-26%') AND "transactions_1k"."ProductID" <= 10