SELECT MAX("transactions_1k"."CardID") as agg_0, MAX("transactions_1k"."GasStationID") as agg_1 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE ("products"."Description" NOT LIKE '%Mýt%né%' OR ("products"."ProductID" <= 333 AND "products"."Description" = 'Kilometer accounting (VAT-free)')) AND "transactions_1k"."Date" LIKE '%2%012-08-26%' AND "customers"."Currency" != 'CZK' AND "transactions_1k"."Date" IN ('2012-08-24') AND "transactions_1k"."TransactionID" >= 144