SELECT AVG("products"."ProductID") as agg_0 FROM "customers" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "products"."Description" IN ('Vratné obaly', 'Potraviny', 'Provozní poplatek', 'Service charge', 'Tisk reportu', 'Servisní poplatek', 'Katalyzátor', 'Ostatné sl.')