SELECT MAX("customers"."CustomerID" + "yearmonth"."Consumption") as agg_0, SUM("customers"."CustomerID" + "yearmonth"."CustomerID") as agg_1, COUNT(*) as agg_2 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID"  WHERE "customers"."Currency" IN ('EUR') AND "customers"."Segment" IN ('SME') AND "customers"."Currency" IN ('EUR') AND "yearmonth"."Consumption" <= 2173.3080428495555 AND "yearmonth"."Date" = 201307