SELECT MAX("customers"."CustomerID" + "yearmonth"."Consumption") as agg_0, AVG("yearmonth"."Date" + "yearmonth"."CustomerID") as agg_1, AVG("customers"."CustomerID") as agg_2 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID"  WHERE ("customers"."Currency" != 'EUR' OR "customers"."CustomerID" <= 1493305 OR "customers"."Currency" IN ('EUR')) AND "customers"."Currency" LIKE '%EUR%' AND ("yearmonth"."Date" <= 201307 OR "yearmonth"."CustomerID" BETWEEN 617575 AND 1597108)