SELECT SUM("transactions_1k"."CardID") as agg_0 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID"  WHERE "transactions_1k"."Date" != '2012-08-25' AND ("gasstations"."Country" NOT LIKE '%CZ%E%' OR ("gasstations"."Country" IN ('SVK') AND "gasstations"."Country" = 'CZE')) AND "gasstations"."ChainID" >= 7 AND ("gasstations"."GasStationID" <= 2847 OR "gasstations"."GasStationID" >= 2491 OR "gasstations"."GasStationID" <= 5310) AND "gasstations"."Segment" = 'Premium'