SELECT SUM("transactions_1k"."TransactionID" + "customers"."CustomerID") as agg_0, AVG("customers"."CustomerID" + "transactions_1k"."CustomerID") as agg_1, MIN("transactions_1k"."TransactionID" + "yearmonth"."Consumption") as agg_2 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "products"."Description" LIKE '%PL%' AND "products"."Description" LIKE '%Supe%r%'