SELECT AVG("yearmonth"."CustomerID" + "transactions_1k"."TransactionID") as agg_0, AVG("transactions_1k"."Amount") as agg_1, SUM("transactions_1k"."Amount") as agg_2 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID"  WHERE "customers"."Segment" IN ('KAM') AND "customers"."Currency" NOT LIKE '%EUR%' AND "customers"."Currency" != 'CZK' AND "transactions_1k"."Price" BETWEEN 71.20300634481654 AND 816.9088874159539 AND "transactions_1k"."CustomerID" <= 120427