SELECT COUNT(*) as agg_0 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "customers"."CustomerID" BETWEEN 548483 AND 1117026 AND "transactions_1k"."Date" NOT LIKE '%2012-08%-26%' AND "products"."Description" NOT LIKE '%VAT%' AND "products"."Description" IN ('Service charge', 'Servisní poplatek', 'Vratné obaly', 'Katalyzátor')