SELECT AVG("gasstations"."ChainID") as agg_0 FROM "gasstations"  WHERE ("gasstations"."Segment" NOT LIKE '%Value%' OR "gasstations"."Segment" IN ('Noname', 'Premium', 'Discount')) AND "gasstations"."Country" = 'CZE' AND "gasstations"."GasStationID" >= 80 AND "gasstations"."Segment" != 'Value for money' AND "gasstations"."ChainID" BETWEEN 2 AND 3 AND "gasstations"."Country" NOT LIKE '%CZE%'