SELECT COUNT(*) as agg_0 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "transactions_1k"."Date" NOT LIKE '%2012-08%-25%' AND "products"."Description" IN ('Umývanie voz', 'Ostatné sl.', 'Potraviny') AND "products"."ProductID" >= 515