SELECT AVG("transactions_1k"."GasStationID") as agg_0, AVG("transactions_1k"."ProductID" + "transactions_1k"."Amount") as agg_1, MIN("transactions_1k"."Price" + "yearmonth"."CustomerID") as agg_2 FROM "customers" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "products"."Description" IN ('Provozní poplatek', 'Vratné obaly', 'Servisní poplatek') AND "customers"."Segment" NOT LIKE '%S%ME%' AND "customers"."Currency" NOT LIKE '%CZ%K%' AND "transactions_1k"."Date" NOT LIKE '%2012-08-25%'