SELECT AVG("transactions_1k"."Price") as agg_0 FROM "transactions_1k" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "transactions_1k"."Date" = '2012-08-23' OR "transactions_1k"."CustomerID" <= 612227 OR "transactions_1k"."Date" = '2012-08-25'