SELECT SUM("transactions_1k"."Amount" + "products"."ProductID") as agg_0, MIN("transactions_1k"."Price" + "gasstations"."GasStationID") as agg_1 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "products"."Description" != 'Zmena parametru' AND "gasstations"."Segment" NOT LIKE '%Noname%' AND ("yearmonth"."Date" BETWEEN 201128 AND 201307 OR "yearmonth"."Date" != 201304 OR "yearmonth"."Date" != 201204) AND "transactions_1k"."CardID" <= 418118 AND "gasstations"."GasStationID" BETWEEN 1902 AND 3349 AND ("customers"."Currency" LIKE '%CZ%K%' OR "customers"."Currency" IN ('EUR') OR "customers"."Currency" != 'EUR')