SELECT SUM("gasstations"."GasStationID") as agg_0, AVG("customers"."CustomerID") as agg_1, COUNT(*) as agg_2 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "products"."Description" LIKE '%VAT)%' OR ("products"."ProductID" BETWEEN 14 AND 75 AND "products"."Description" IN ('Ostatni zbozi', 'Um√Ωvanie voz'))