SELECT MIN("customers"."CustomerID") as agg_0, AVG("transactions_1k"."Amount" + "transactions_1k"."ProductID") as agg_1, AVG("transactions_1k"."Price") as agg_2 FROM "transactions_1k" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "products"."Description" NOT LIKE '%VA%T%' AND "customers"."Segment" NOT LIKE '%SM%E%' AND "products"."Description" NOT LIKE '%of%' AND "customers"."Currency" IN ('EUR') AND "transactions_1k"."Date" IN ('2012-08-23')