SELECT COUNT(*) as agg_0 FROM "customers" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "yearmonth"."CustomerID" <= 697056 AND "gasstations"."Segment" LIKE '%mone%y%'