SELECT SUM("transactions_1k"."Amount") as agg_0, COUNT(*) as agg_1, SUM("transactions_1k"."TransactionID") as agg_2 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID"  WHERE ("products"."Description" LIKE '%Ca%rd%' OR "products"."ProductID" >= 169 OR "products"."Description" LIKE '%L%iber-t%') AND "transactions_1k"."Date" = '2012-08-23' AND "transactions_1k"."Date" = '2012-08-26'