SELECT AVG("transactions_1k"."Amount" + "transactions_1k"."ProductID") as agg_0 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "gasstations"."ChainID" >= 4 AND "gasstations"."Segment" IN ('Discount', 'Noname', 'Premium') AND "products"."Description" LIKE '%fr%ee)%' AND ("gasstations"."GasStationID" <= 4126 OR ("gasstations"."Country" LIKE '%CZE%' AND "gasstations"."GasStationID" <= 4323)) AND "transactions_1k"."Date" IN ('2012-08-23', '2012-08-25') AND "transactions_1k"."Date" NOT LIKE '%2012-08-26%'