SELECT MIN("transactions_1k"."ProductID") as agg_0, SUM("transactions_1k"."ProductID" + "products"."ProductID") as agg_1 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "products"."ProductID" BETWEEN 60 AND 567 AND "customers"."Currency" NOT LIKE '%CZ%K%' AND "products"."Description" NOT LIKE '%To%ll%' AND "customers"."Segment" LIKE '%K%AM%'