SELECT SUM("customers"."CustomerID" + "transactions_1k"."ProductID") as agg_0, MIN("transactions_1k"."GasStationID") as agg_1 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "transactions_1k"."CardID" <= 627722 AND "transactions_1k"."Date" IN ('2012-08-24', '2012-08-25') AND "customers"."Currency" != 'CZK' AND ("products"."ProductID" >= 162 OR "products"."Description" NOT LIKE '%CC%S%' OR "products"."ProductID" BETWEEN 375 AND 503) AND ("transactions_1k"."GasStationID" >= 749 OR "transactions_1k"."GasStationID" BETWEEN 1104 AND 5365) AND "transactions_1k"."Price" <= 16.203854314736752