SELECT COUNT(*) as agg_0, MIN("transactions_1k"."TransactionID") as agg_1, AVG("transactions_1k"."Price" + "gasstations"."GasStationID") as agg_2 FROM "transactions_1k" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "gasstations"."Segment" NOT LIKE '%Di%scount%'