SELECT MIN("transactions_1k"."CustomerID" + "transactions_1k"."GasStationID") as agg_0 FROM "transactions_1k" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "products"."Description" = 'Natural Plus' AND "customers"."Segment" IN ('LAM') AND "transactions_1k"."Date" NOT LIKE '%2012-08-%26%' AND "products"."Description" NOT LIKE '%Carnet%' AND "customers"."Segment" LIKE '%LAM%'