SELECT SUM("transactions_1k"."CustomerID" + "transactions_1k"."TransactionID") as agg_0, COUNT(*) as agg_1 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID"  WHERE "transactions_1k"."TransactionID" BETWEEN 331 AND 924 AND "customers"."Segment" NOT LIKE '%LAM%' AND "transactions_1k"."Date" LIKE '%2012%-08-25%' AND "customers"."CustomerID" <= 300597 AND "transactions_1k"."Date" NOT LIKE '%2012-08-25%'