SELECT COUNT(*) as agg_0, SUM("yearmonth"."CustomerID") as agg_1 FROM "transactions_1k" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "yearmonth"."Date" BETWEEN 201245 AND 201308 AND ("transactions_1k"."Date" NOT LIKE '%2012-%08-25%' OR "transactions_1k"."Date" NOT LIKE '%2%012-08-25%') AND "products"."Description" = 'UTA-Box (blocking)'