SELECT SUM("transactions_1k"."Amount" + "transactions_1k"."GasStationID") as agg_0, MAX("transactions_1k"."Amount") as agg_1, AVG("transactions_1k"."TransactionID" + "transactions_1k"."Price") as agg_2 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID"  WHERE "products"."Description" IN ('Servisní poplatek', 'Umývanie voz', 'Service charge', 'Vratné obaly', 'Provozní poplatek', 'Ostatni zbozi', 'Potraviny') AND "transactions_1k"."Date" != '2012-08-25'