SELECT MIN("transactions_1k"."GasStationID") as agg_0 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "products"."ProductID" BETWEEN 29 AND 611 AND ("products"."Description" NOT LIKE '%Servisní%' OR "products"."Description" LIKE '%D%EU%' OR "products"."Description" IN ('Ostatni zbozi', 'Provozní poplatek', 'Katalyzátor', 'Potraviny', 'Tisk reportu', 'Umývanie voz', 'Vratné obaly'))