SELECT SUM("yearmonth"."Consumption") as agg_0, MIN("yearmonth"."Consumption") as agg_1, MAX("transactions_1k"."TransactionID" + "customers"."CustomerID") as agg_2 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE ("transactions_1k"."Date" = '2012-08-23' OR "transactions_1k"."Amount" != 16) AND "customers"."Segment" LIKE '%SM%E%' AND "customers"."Currency" LIKE '%C%ZK%'