SELECT AVG("products"."ProductID" + "transactions_1k"."TransactionID") as agg_0 FROM "customers" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "customers"."Segment" IN ('SME', 'KAM') AND "gasstations"."Segment" LIKE '%Premiu%m%' AND "products"."Description" = 'Fee for bank charges' AND ("transactions_1k"."ProductID" = 3 OR "transactions_1k"."TransactionID" BETWEEN 114 AND 489) AND "products"."ProductID" >= 567