SELECT AVG("transactions_1k"."CardID" + "transactions_1k"."CustomerID") as agg_0 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "products"."ProductID" <= 44 AND "customers"."Currency" LIKE '%CZK%' AND "customers"."Currency" != 'CZK' AND "gasstations"."Country" LIKE '%SV%K%' AND ("transactions_1k"."Date" LIKE '%2012%-08-25%' OR "transactions_1k"."CustomerID" <= 1488905) AND "transactions_1k"."ProductID" != 5