SELECT SUM("yearmonth"."Consumption") as agg_0, AVG("transactions_1k"."CustomerID") as agg_1 FROM "customers" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "transactions_1k"."ProductID" >= 2 AND ("gasstations"."Country" != 'CZE' OR ("gasstations"."Country" IN ('SVK') AND "gasstations"."Country" LIKE '%CZ%E%')) AND "customers"."Segment" = 'SME' AND "yearmonth"."Consumption" BETWEEN 3071.2777697416664 AND 8356.307243921607