SELECT SUM("transactions_1k"."CardID") as agg_0 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "transactions_1k"."Price" <= 3859.5670331300103 AND "gasstations"."Segment" IN ('Noname') AND "transactions_1k"."Amount" BETWEEN 0 AND 4 AND "transactions_1k"."Time" IN ('11:55:00', '06:17:00', '15:23:00', '07:53:00', '16:04:00', '08:24:00')