SELECT SUM("yearmonth"."Date") as agg_0, MAX("transactions_1k"."CustomerID") as agg_1 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "customers"."Currency" IN ('EUR') AND "gasstations"."ChainID" BETWEEN 3 AND 17 AND "transactions_1k"."CardID" BETWEEN 635916 AND 638158