SELECT MAX("transactions_1k"."TransactionID" + "transactions_1k"."GasStationID") as agg_0, COUNT(*) as agg_1 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID"  WHERE "transactions_1k"."Date" IN ('2012-08-24', '2012-08-26') AND "products"."Description" != 'Natural normal' AND "transactions_1k"."Time" IN ('08:26:00', '07:53:00', '09:50:00', '11:55:00') AND "transactions_1k"."GasStationID" <= 5066