SELECT MAX("transactions_1k"."CardID" + "transactions_1k"."Price") as agg_0, AVG("gasstations"."ChainID" + "transactions_1k"."Price") as agg_1, MIN("transactions_1k"."CustomerID" + "transactions_1k"."Amount") as agg_2 FROM "transactions_1k" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "gasstations"."Country" IN ('SVK') AND "gasstations"."Segment" LIKE '%Value%'