SELECT MIN("gasstations"."ChainID") as agg_0, AVG("yearmonth"."Consumption" + "gasstations"."GasStationID") as agg_1, COUNT(*) as agg_2 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "transactions_1k"."CardID" >= 331830 AND "yearmonth"."CustomerID" BETWEEN 1185244 AND 1478699 AND "transactions_1k"."ProductID" >= 14