SELECT MAX("transactions_1k"."Price" + "transactions_1k"."Amount") as agg_0, SUM("transactions_1k"."CustomerID") as agg_1, MAX("transactions_1k"."Amount") as agg_2 FROM "transactions_1k" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "transactions_1k"."Date" NOT LIKE '%201%2-08-24%' AND "products"."Description" NOT LIKE '%P%L%' AND "products"."Description" != 'Kartog. tov.' AND "products"."ProductID" BETWEEN 330 AND 520