SELECT MIN("transactions_1k"."Amount") as agg_0, COUNT(*) as agg_1, AVG("transactions_1k"."ProductID" + "transactions_1k"."CustomerID") as agg_2 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID"  WHERE "transactions_1k"."Date" LIKE '%2012-08-24%' AND ("gasstations"."Country" NOT LIKE '%SVK%' OR ("gasstations"."Segment" IN ('Value for money', 'Discount') AND "gasstations"."Country" = 'SVK'))